package date0132021;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x;
		x=100;
		x +=10;
		System.out.println("x:" + x);
		x-=10;
		System.out.println("x:"+x );
		x*=10;
		System.out.println("x:"+x );
        x/=20;
		System.out.println("x:"+x );
        x%=4;
		System.out.println("x:"+x );

	}

}
